mapper.select:
SELECT COUNT(B.ID) AS totalCount
  FROM board B
 WHERE B.TYPE = #{type}

SELECT ROW_NUMBER() OVER(PARTITION BY B.TYPE ORDER BY B.ID) AS no , B.ID AS boardId , B.USER_ID AS userId , U.USER_NAME AS userName , U.EMAIL AS email , B.TITLE AS title , B.VIEWS AS views , DATE_FORMAT(B.CREATED_DATE, '%Y-%m-%d') AS createDate FROM board B INNER JOIN users U ON B.USER_ID = U.USER_ID WHERE B.TYPE = #{type}
ORDER BY ${sortColumn} ${sortDir}
LIMIT ${pageNum}, ${pageSize}

mapper.update:
UPDATE board
   SET VIEWS = VIEWS + 1
 WHERE TYPE = #{type}
   AND ID = #{id}

UPDATE board
   SET TITLE = #{title}
     , CONTENT = #{content}
     , UPDATED_DATE = NOW()
     , UPDATED_BY = #{userId}
 WHERE TYPE = #{type}
   AND ID = #{boardId}

mapper.delete:
DELETE FROM board
 WHERE TYPE = #{type}
   AND ID = #{boardId}
